import styles from './User.module.css';
import Button from './Button';
import { useState } from 'react';

const User = props => {
    const [name, setName] = useState('');
    const [age, setAge] = useState('');
    const [isValidName, setIsValidName] = useState(false);
    const [isValidAge, setIsValidAge] = useState(false);
    const [isClicked, setIsClicked] = useState(false);

    const nameChangeHandler = event => {
        if (event.target.value.trim().length === 0) {
            setIsValidName(false);
            return;
        }
        setName(event.target.value);
    }

    const numberChangeHandler = event => {
        if (event.target.value < 0) {
            setIsValidAge(false);
            return;
        }
        setAge(event.target.value);
    }

    const buttonisClicked = props => { 

    }

    const submitHandler = event => {
        event.preventDefault();
        const submission = {
            userName: name,
            userAge: age
        }

        props.submissionData(submission);
        setName('');
        setAge('');
    }

    // let errorMessage = null;

    // if (!isValidName || !isValidAge) {
    //     errorMessage = null;
    // }

    return (
        <form onSubmit={submitHandler}>
            <div className={styles['form-control']}>
                <div>
                    <label >Username</label>
                    <input type='text' onChange={nameChangeHandler}></input>
                </div>
                <div>
                    <label>Age(Years)</label>
                    <input type='number' onChange={numberChangeHandler}></input>
                </div>
                <Button type="submit" onClick={buttonisClicked}>Add User</Button>
            </div>
        </form>
    );
}

export default User;
