import styles from './User.module.css';
import Button from './Button';
import { useState } from 'react';
import ErrorMessage from './ErrorMessage';

const messages = ['Wrong name', 'Wrong age'];

const User = props => {
    const [name, setName] = useState('');
    const [age, setAge] = useState('');
    const [isValidName, setIsValidName] = useState(false);
    const [isValidAge, setIsValidAge] = useState(false);
    const [isClicked, setIsClicked] = useState(false);
    const [message, setMessage] = useState(messages);

    const nameChangeHandler = event => {
        if (event.target.value.trim().length === 0) {
            setIsValidName(false);
            setMessage(messages[0]);
            return;
        }
        setIsValidName(true);
        setName(event.target.value);
    }

    const numberChangeHandler = event => {
        if (event.target.value < 0) {
            setIsValidAge(false);
            setMessage(messages[1]);
        } else {
            setIsValidAge(true);
        }
        setAge(event.target.value);
    }

    const buttonIsClicked = () => {
        setIsClicked(true);
        console.log('Clicked');
    }

    const itIsPressed = () => {
        setIsClicked(false);
    }

    const submitHandler = event => {
        event.preventDefault();
        const submission = {
            userName: name,
            userAge: age
        }

        if (!isValidName || !isValidAge) {
            return;
        }

        setName('');
        setAge('');
        props.submissionData(submission);
        setIsClicked(false);
        setIsValidName(false);
        setIsValidAge(false);
    }

    // const showingMessage = () => {
    //     if (!isValidName) {
    //         setMessage(messages[0]);
    //     } else if (!isValidAge) {
    //         setMessage(messages[1]);
    //     }
    // }

    return (
        <form onSubmit={submitHandler}>
            <div className={styles['form-control']}>
                <div>
                    <label>Username</label>
                    <input type='text' value={name} onChange={nameChangeHandler}></input>
                </div>
                <div>
                    <label>Age</label>
                    <input type='number' value={age} onChange={numberChangeHandler}></input>
                </div>
                <Button type='submit' onClick={buttonIsClicked}>Add User</Button>
            </div>
            <div>
                {isClicked && (!isValidName || !isValidAge) && <ErrorMessage pressed={itIsPressed} message={message} />}
            </div>
        </form>
    );
}

export default User;
